var progressBar;

function initBar(){
	if(progressBar==undefined){
		var bar_id = 'progress_bar';
		progressBar = document.getElementById(bar_id);
	}
	return progressBar;
}

function updateUploadDialogProgressBar(value){
	initBar();
	if(progressBar!==undefined){
 		progressBar.value = value;
 		progressBar.getElementsByTagName('span')[0].innerHTML = value + " %";
	}
}